import { SignIn } from 'reduxStore/actions/app';

const INITIAL_STATE = {
  init: false,
  fetching: false,
  user: null,
};

export default (state = INITIAL_STATE, { type, payload }) => {
  switch (type) {
    case SignIn.REQUEST: {
      return {
        ...state,
        fetching: true,
      };
    }

    case SignIn.SUCCESS: {
      return {
        ...state,
        user: payload,
      };
    }

    case SignIn.FULFILL: {
      return {
        ...state,
        fetching: false,
      };
    }

    default:
      return state;
  }
};
